import {Component, OnInit} from '@angular/core';
import {Reserva} from 'src/app/models/reserva.model'
import {ReservaService} from "../../services/reserva.service";

@Component({
  selector: 'app-reserva-lista',
  templateUrl: './reserva-lista.component.html',
  styleUrls: ['./reserva-lista.component.scss']
})
export class ReservaListaComponent implements OnInit{
  reservas: Reserva[] = [];

  constructor(
    private reservaService: ReservaService
  ) {}

  ngOnInit(): void{
    this.loadDataIntoTable()
    /*this.reservaService.getReservas().subscribe(
      (dados: Reserva[])=>{
        this.reservas = dados;
      }
    );*/
  }

  private loadDataIntoTable(): void {
    this.reservaService.getReservas().subscribe(reservas => {
        this.reservas = reservas;
      },
      error => {
        console.error('Erro ao buscar reservas:', error)
      })
  }
}
<div class="table-responsive mb -5">
  <table class="table">
    <thead>
    <tr>
      <th scope="col">Sala</th>
      <th scope="col">Responsável</th>
      <th scope="col">Início</th>
      <th scope="col">Fim</th>
    </tr>
    </thead>
    <tbody>
    <ng-container >
      <tr *ngFor="let reserva of reservas">
        <td>{{reserva.sala_id}}</td>
        <td>{{reserva.responsavel}}</td>
        <td>{{reserva.inicio}}</td>
        <td>{{reserva.fim}}</td>
      </tr>
    </ng-container>
    </tbody>
  </table>

</div>
